prefix = ''
suffix = ''
prefixes = [ prefix[ i:i+3 ] for i in range( 0,len( prefix ),3 ) ]
suffixes = [ suffix[ i:i+3 ] for i in range( 0,len( suffix ),3 ) ]

with open( 'corncob_lowercase.txt','r' ) as wordfile:
    worddata = wordfile.read()
words = [ word.strip() for word in worddata.split( '\n' ) ]
words6 = list( filter( lambda x: len(x)==6,words ) )

results = []
from itertools import product
for p,s in product( prefixes,suffixes ):
    if p+s in words6:
        results.append( p+s )
results.sort()
print( results )